import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import './App.css';

const API_KEY = '96d39d7c';
const API_URL = 'http://www.omdbapi.com/';
const API_SEARCH_ACTOR_URL = 'https://www.omdbapi.com/';

const searchMovies = async (query, searchByActor = false) => {
    const searchURL = searchByActor ? API_SEARCH_ACTOR_URL : API_URL;
    const searchParam = searchByActor ? 'a' : 's';

    const response = await fetch(`${searchURL}?${searchParam}=${query}&apikey=${API_KEY}`);
    const data = await response.json();
    if (data.Error) {
        console.error(data.Error);
        return [];
    } else {
        return data.Search;
    }
};

const SearchForm = ({ onSearch }) => {
    const [query, setQuery] = useState('');

    const handleSubmit = (event) => {
        event.preventDefault();
        onSearch(query);
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                placeholder="Search for a movie or actor"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
            />
            <button type="submit">Search</button>
        </form>
    );
};

const MovieList = ({ movies }) => {
    return (
        <ul className="movie-list">
            {movies.map((movie) => (
                <li key={movie.imdbID} className="movie-item">
                    <img
                        src={movie.Poster}
                        alt={`${movie.Title} poster`}
                        className="movie-poster"
                    />
                    <div className="movie-details">
                        <h2 className="movie-title">{movie.Title}</h2>
                        <p className="movie-year">{movie.Year}</p>
                    </div>
                </li>
            ))}
        </ul>
    );
};


const SortDropdown = ({ onSort }) => {
    const handleSortChange = (e) => {
        onSort(e.target.value);
    };

    return (
        <select onChange={handleSortChange}>
            <option value="relevance">Relevance</option>
            <option value="year">Year</option>
            <option value="alphabetical">Alphabetical</option>
        </select>
    );
};

const MovieSearch = () => {
    const [movies, setMovies] = useState([]);
    const [originalMovies, setOriginalMovies] = useState([]);

    const handleSearch = async (query) => {
        const results = await searchMovies(query);
        setMovies(results);
        setOriginalMovies(results);
        localStorage.setItem('movies', JSON.stringify(results));
    };

    const handleSort = (sortOption) => {
        switch (sortOption) {
            case 'year':
                setMovies([...movies].sort((a, b) => a.Year - b.Year));
                break;
            case 'alphabetical':
                setMovies([...movies].sort((a, b) => a.Title.localeCompare(b.Title)));
                break;
            case 'relevance':
                setMovies([...originalMovies]);
                break;
            default:
                break;
        }
    };

    useEffect(() => {
        const storedMovies = JSON.parse(localStorage.getItem('movies'));
        if (storedMovies) {
            setMovies(storedMovies);
            setOriginalMovies(storedMovies);
        }
    }, []);

    return (
        <Router>
            <div className="App">
                <style>
                    {`
            .movie-list {
              display: flex;
              flex-wrap: wrap;
              gap: 1rem;
              list-style-type: none;
              margin: 0;
              padding: 0;
            }

            .movie-item {
              background-color: #fff;
              border: 1px solid #e0e0e0;
              border-radius: 4px;
              display: flex;
              flex-direction: column;
              overflow: hidden;
              padding: 1rem;
              width: 300px;
            }

            .movie-poster {
              align-self: center;
              max-height: 350px;
              max-width: 100%;
              object-fit: contain;
            }

            .movie-details {
              display: flex;
              flex-direction: column;
              align-items: center;
            }

            .movie-title {
              font-size: 18px;
              font-weight: bold;
              margin: 1rem 0;
              text-align: center;
            }

            .movie-year {
              font-size: 14px;
              font-weight: normal;
              margin: 0;
              text-align: center;
            }

            .movie-poster {
  align-self: center;
  max-height: 350px;
  max-width: 100%;
  object-fit: contain;
  transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.movie-poster:hover {
  transform: scale(1.1);
  box-shadow: 0 0 30px rgba(0, 0, 0, 0.3);
}

          `}
                </style>
                <h1>Movie Search</h1>
                <Routes>
                    <Route
                        path="/"
                        element={
                            <div>
                                <SearchForm onSearch={handleSearch} />
                                <SortDropdown onSort={handleSort} />
                                <MovieList movies={movies} />
                            </div>
                        }
                    />
                </Routes>
            </div>
        </Router>
    );
};

export default MovieSearch;
